mapper.select:
SELECT A.user_id 
	FROM system_user A 
WHERE A.username=#{username}

SELECT A.user_id,A.username,A.`password`,A.xm,A.idcard,A.mphone,A.islock,A.bz,A.`status`, IFNULL(C.role_id,0) role_id,IFNULL(C.role_name,'') role_name, IFNULL(D.cust_id,0) cust_id,IFNULL(D.cust_name,'')cust_name FROM system_user A LEFT JOIN system_user_role B ON A.user_id=B.user_id LEFT JOIN system_role C ON B.role_id=C.role_id LEFT JOIN customer D ON B.cust_id=D.cust_id WHERE A.`status`='AVAILABLE'
AND A.username LIKE #{_username}
ORDER BY A.username
LIMIT #{start},#{limit}

mapper.update:
UPDATE system_user_role A
	SET A.role_id=#{role_id},
		A.cust_id=#{cust_id}
WHERE A.user_id=#{user_id}
