mapper.select:
SELECT A.menu_id,A.cust_id,A.name,A.menulevcod,A.supmenucode,A.`key`,A.`type`,A.sort
	FROM customer_menu A
WHERE A.menu_id=#{menu_id}

SELECT A.menu_id,A.cust_id,A.name,A.menulevcod,A.supmenucode,A.`key`,A.`type`,A.sort
	FROM customer_menu A
WHERE A.cust_id=#{cust_id}
	AND A.`key`=#{key}

SELECT A.menu_id,A.cust_id,A.name,A.menulevcod,A.supmenucode,A.`key`,A.`type`,A.sort
	FROM customer_menu A
WHERE A.menulevcod=#{level}
	AND A.cust_id=#{cust_id}
ORDER BY A.sort
