mapper.delete:
DELETE FROM media 
	WHERE id IN

	#{ids}

DELETE FROM media_sync 
	WHERE m_id=#{m_id}
AND service_type=#{service_type}

mapper.select:
SELECT A.id,A.cust_id,A.media_type,A.media_size,A.real_name,A.display_name,A.media_path,A.timestamp
	FROM media A
WHERE A.id=#{id}

SELECT A.id,A.m_id,A.cust_id,A.service_type,A.media_id,A.created_at,A.expire_time,A.timestamp
	FROM media_sync A
WHERE A.m_id=#{m_id}

	AND A.service_type=#{service_type}

SELECT A.id,A.m_id,A.cust_id,A.service_type,A.media_id,A.created_at,A.expire_time,A.timestamp FROM media_sync A WHERE A.m_id IN
#{media_ids}
ORDER BY A.m_id

mapper.update:
UPDATE media A
	SET A.display_name=#{display_name},
		A.media_path=#{media_path}
	WHERE A.id=#{id}

mapper.select:

SELECT A.id,A.cust_id,A.media_type,A.media_size,A.real_name,A.display_name,A.media_path,A.timestamp
	FROM media A
WHERE A.cust_id=#{cust_id}
	
		AND A.display_name LIKE #{_display_name}
	
	
		LIMIT #{start},#{limit}


SELECT COUNT(1) FROM
	media A
WHERE A.cust_id=#{cust_id}
	
		AND A.display_name LIKE #{_display_name}
